import { createContext ,useState,useEffect} from "react";
import axios from "axios"

import jwt_decode from "jwt-decode";
export const AuthContext=createContext();

export const AuthContextProvider=({children})=>{
   localStorage.getItem('authToken')?JSON.parse(localStorage.getItem('authToken')):null
   let [authToken,SetauthTokens]=useState()
   let [user,Setuser]=useState(null)
   let userLogin=async (e)=>{
    e.preventDefault()

    console.log(e)
    let username=e.target.username.value
    let password=e.target.password.value
    let data={username,password}
    // console.log(data);
    console.log("form submmitted")
    const response =await  axios
      .post(`http://127.0.0.1:8000/token/`, data, {
          headers: {
              "Content-Type": "application/json",
          },
      });


      if(response.status===200){
        let data1=await response.data;
        await SetauthTokens(data1)
        await Setuser(jwt_decode(data1.access))
        localStorage.setItem('authToken',JSON.stringify(data1))
      }else{
        alert('Something went Wrong! :(')
      }

   }
   let contextData={
     user:user,
     loginUser:userLogin
   }
    return (
        <AuthContext.Provider
          value={contextData}
        >
          {children}
        </AuthContext.Provider>
      );


}
