import axios from "axios";
import React ,{useState,useContext,useEffect} from "react";
import NavBar from "../components/NavBar";
import { AuthContext } from "../contexts/AuthContext";
// useParams
import { Form, 
    Container,
     Col,
      Row,
       Card, Nav, Navbar, ButtonGroup,Modal,Button } from 'react-bootstrap'
import { useParams } from "react-router-dom";
function TweetDetail(){
    const {id}=useParams()
    const {authToken}=useContext(AuthContext)
    const [data,Setdata]=useState()
    const {content,setContent}=useState()
    async function getData(){
        try{

            const Response= await axios.get(`http://127.0.0.1:8000/detail/${id}/`,{
                headers:{
                    "Content-Type":"application/json",
                    "Authorization":"Bearer "+String(authToken.access)
                }
            })
            if(Array.isArray(Response.data)){

                Setdata(Response.data)
            }else{
                Setdata([Response.data])
            }

        }catch(e){
            console.log(e)
        }
    }
    useEffect(()=>{
        getData()
    },[])
    async function handleLike(tid) {
        console.log(id)
        try {

          let response = await axios.get(`http://127.0.0.1:8000/like/${id}/`, {
            headers: {
              'Content-Type': 'application/json',
              'Authorization': 'Bearer ' + String(authToken.access)
            }
          })

          // setData(response.data)
        } catch (e) {
          console.log(e)
          // if(user &&e.response.status===401){
          //   logout()
          // }else if(user&&e.response.status<=500){
          //   alert(e.message)
          // }
        }
      }
      async function handleretweet(tid) {
        console.log(id)
        try {

          let response = await axios.post(`http://127.0.0.1:8000/retweet/${id}/`,{} ,{
            headers: {
              'Content-Type': 'application/json',
              'Authorization': 'Bearer ' + String(authToken.access)
            }
          })

          // setData(response.data)
        } catch (e) {
          console.log(e)
          // if(user &&e.response.status===401){
          //   logout()
          // }else if(user&&e.response.status<=500){
          //   alert(e.message)
          // }
        }
      }
      async function handlereply(tid){
        try{
            let data={content}
            console.log(data)
            const response = await axios.post(`http://127.0.0.1:8000/reply/${tid}/`,data,{
                headers:{
                    "Content-Type": "multipart/form-data",
                    'Authorization':"Bearer "+String(authToken.access)
                }
            })
        }catch(e){
            console.log(e)
        }


        // console.log(content)


    }
    if(data){
        console.log(data['0']['parent']['username'])
    }

    return(
        <Row>
            <Col>
                <NavBar/>
            </Col>
            <Col xs={6}>
            <h3 >Tweet</h3>
            < Card key={id} style={{ width: "100%", margin: "10px" }} className='card'>



                  {/* <FaTwitter/> */}

{data['0']['parent']['image'] && <Card.Img variant="top" src={`http://127.0.0.1:8000/${data['0']['parent']['image']}`} />}
                {/* </Link> */}

                <Card.Body>
                <Card.Title>{data['0']['parent']['username']}</Card.Title>

                  {data['0']['parent']['content'] && <Card.Text>
                    {data['0']['parent']['content']}
                  </Card.Text>}
                  </Card.Body>
                  <ButtonGroup aria-label="Basic example" style={{ paddingRight: "20%" }} >
                    <Button className='tweetbtn like btn' onClick={() => handleLike(id)}> <i class="bi bi-heart"></i></Button>
                    <Button className='tweetbtn retweet btn 'onClick={() => handleretweet(id)}><i class="bi bi-arrow-repeat"></i></Button>
                    <Button className='tweetbtn reply btn' onClick={() =>{ handlereply(id)} }><i class="bi bi-reply"></i></Button>
                    <Button className='tweetbtn share btn'><i class="bi bi-share"></i></Button>
                  </ButtonGroup>






              </Card>
            </Col>
            <Col>

            </Col>
        </Row>
    );
}

export default TweetDetail
