import React from "react"
import { Card } from "react-bootstrap"

function ThreadComments(props){
    console.log(props.value)
    return <div>
        {props.value && props.value.map(({ content, id, likes,username,children,parent }) => (
            <Card>
            <Card.Body>

            <Card.Title>{username}</Card.Title>
            <span style={{ color: "grey" }}>Replying to <Link to={`/profile/${parent['username']}`}>{parent['username']}</Link></span>
            {props.content && <Card.Text>
                        {props.content}
                    </Card.Text>}
            </Card.Body>

            </Card>
        ))}
    </div>
}

export default ThreadComments
