import React from 'react';
import Button from 'react-bootstrap/Button';
import { Form, Container, Col, Row, Card, Nav, Navbar, ButtonGroup } from 'react-bootstrap'
import { useState, useEffect, useContext } from "react";
import axios from "axios"
import { Link,Navigate } from "react-router-dom";
import { useNavigate } from "react-router-dom";


// import { LoginContext } from "../contexts/AuthContext";
import { AuthContext } from '../contexts/AuthContext';

function Home() {

  const [data, setData] = useState(null)
  const [content, setContent] = useState("")
  const [image, setImage] = useState(null)
  const { user, authToken, logout } = useContext(AuthContext)
  // console.log(authToken.access)
  async function getdata() {
    // let data;
    try {

      let response = await axios.get(`http://127.0.0.1:8000/`, {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer ' + String(authToken.access)
        }
      })
      setData(response.data)
    } catch (e) {
      // console.log(e)
      if (user && e.response.status === 401) {
        logout()
        return navigate("/");

      } else if (user && e.response.status <= 500) {
        alert(e.message)
      }
    }



  }

  useEffect(() => {
    getdata()
  }, []

  );

  function handleimg(e) {
    // setImgUrl((e.target.files[0]))
    console.log(e.target.files[0])
    setImage(e.target.files[0]);
  }
  function handleSubmit(e) {
    // let productimg=URL.createObjectURL(imgUrl)
    e.preventDefault()
    let data = { content, image }
    console.log(data);
    const myNewModel = axios
      .post(`http://127.0.0.1:8000/createtweet/`, data, {
        headers: {
          "Content-Type": "multipart/form-data",
          'Authorization': 'Bearer ' + String(authToken.access)
        },
      }).then((res) => {
        getdata();
        setImage(null);
        setContent("");
        return res;
      }).catch((error) => {
        return error.response;
      });
  }
  async function handleLike(id) {
    console.log(id)
    try {

      let response = await axios.get(`http://127.0.0.1:8000/like/${id}/`, {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer ' + String(authToken.access)
        }
      })
      getdata()
      // setData(response.data)
    } catch (e) {
      console.log(e)
      // if(user &&e.response.status===401){
      //   logout()
      // }else if(user&&e.response.status<=500){
      //   alert(e.message)
      // }
    }
  }
  if (data) {
    console.log(data)
  }

  return (
    <Container className="App">

      <Row>
        <Col>
          <div className='vertical-nav'>
            <Nav defaultActiveKey="/home" className="flex-column">
              <Navbar.Brand style={{ fontSize: "30px", margin: "10px" }}>My Tweet</Navbar.Brand>
              <Nav.Item style={{ fontSize: "30px", margin: "10px" }}><Link style={{ textDecoration: "none" }} to="/"><i class="bi bi-house"></i> Home</Link> </Nav.Item>
              <Nav.Item eventKey="disabled" style={{ fontSize: "30px", margin: "10px" }}>
                <i class="bi bi-chat-left-text"></i> Messages
              </Nav.Item>
              <Nav.Item eventKey="disabled" style={{ fontSize: "30px", margin: "10px" }}>
                <i class="bi bi-person"></i> Profile
              </Nav.Item>
              <Nav.Item eventKey="link-1" style={{ fontSize: "30px", margin: "10px" }}>{user ? <Link style={{ textDecoration: "none" }} to="/logout"><i class="bi bi-box-arrow-left"></i> Logout</Link> : <Link style={{ textDecoration: "none" }} to="/signin"><i class="bi bi-box-arrow-in-right"></i> Login</Link>} </Nav.Item>
              <Nav.Item eventKey="link-2" style={{ fontSize: "30px", margin: "10px" }}>Link</Nav.Item>
            </Nav>

          </div>
        </Col>
        <Col xs={6}>
          <h3 >Home</h3>
          {user && <h5>{user.username}</h5>}
          {user && <Form style={{ width: "100%", margin: "10px" }}>
            <Form.Group className="mb-3" controlId="formBasicEmail">
              <Form.Label>Tweet Content</Form.Label>
              <Form.Control as="textarea" rows="3" placeholder="Type here ....." value={content} onChange={(e) => setContent(e.target.value)}>

              </Form.Control>

            </Form.Group>


            <Form.Group className="mb-3" controlId="formBasicfile">
              <Form.Label>Insert Image</Form.Label>
              <Form.Control type="file" accept="image/jpeg,image/png,image/gif" onChange={(e) => handleimg(e)} />
            </Form.Group>
            <Button variant="primary" className='btn' type="submit" onClick={(e) => handleSubmit(e)}>
              Tweet
            </Button>
          </Form>}



          <div>

            {data && data.map(({ image, content, id, likes }) => (
              <Card key={id} style={{ width: "100%", margin: "10px" }} className='card'>
                {image && <Card.Img variant="top" src={`http://127.0.0.1:8000/${image}`} />}

                <Card.Body>
                  {/* <Card.Title>{user}</Card.Title> */}
                  {content && <Card.Text>
                    {content}
                  </Card.Text>}

                  <ButtonGroup aria-label="Basic example" style={{ paddingRight: "20%" }} >
                    <Button className='tweetbtn like btn' onClick={() => handleLike(id)}><i>{likes}<i class="bi bi-heart"></i></i></Button>
                    <Button className='tweetbtn retweet btn '><i class="bi bi-arrow-repeat"></i></Button>
                    <Button className='tweetbtn reply btn'><i class="bi bi-reply"></i></Button>
                    <Button className='tweetbtn share btn'><i class="bi bi-share"></i></Button>

                  </ButtonGroup>
                </Card.Body>
              </Card>
            ))}
          </div>
        </Col>
        <Col>3 of 3</Col>
      </Row>

    </Container>

  );
}

export default Home;
