                    {/* <Button className='btn tweetbtn reply' onClick={() => { handlereply(id) }}><i class="bi bi-reply"></i></Button> */}
                    <Button className='btn tweetbtn share' ><i class="bi bi-share"></i></Button>
            </ButtonGroup>
                    </div>

                <Card style={{marginLeft:"20px",}}>
                <Card.Body>

            <Card.Title>{username}</Card.Title>
            <span style={{ color: "grey" }}>Replying to <Link to={`/profile/${parent['username']}`}>{parent['username']}</Link></span>
            {content && <Card.Text>
                        {content}
                    </Card.Text>}
            </Card.Body>

                </Card>
                </div>
            ))}


            </div>
        ))}
    </div>
}

export default ThreadComments
