import React from 'react';
import Button from 'react-bootstrap/Button';
import {Form,Container,Col,Row,Card} from 'react-bootstrap'
import {useState,useEffect,useContext} from "react";
import axios from "axios"
// import { LoginContext } from "../contexts/AuthContext";
import { AuthContext } from '../contexts/AuthContext';

function Home() {
  const [data,setData]=useState(null)
  const [content,setContent]=useState("")
  const [image,setImage]=useState(null)
  const {user,authToken}=useContext(AuthContext)
  // console.log(authToken.access)
  async function  getdata (){
    // let data;
   let response=await axios.get(`http://127.0.0.1:8000/`,{
     headers:{
       'Content-Type':'application/json',
       'Authorization':'Bearer '+String(authToken.access)
     }
   })
   if(response.status===200){
     setData(response.data)
   }else{

   }
      // .then(res => {
      //   const tweets = res.data;
      //   // data=res.data;
      //   setData(tweets)
      // })


  }

  useEffect(()=>{
    getdata()},[]

  );

  function handleimg(e){
    // setImgUrl((e.target.files[0]))
    console.log(e.target.files[0])
    setImage(e.target.files[0]);
  }
  function handleSubmit(e){
      // let productimg=URL.createObjectURL(imgUrl)
      e.preventDefault()
    let data={content,image}
    console.log(data);
    const myNewModel =  axios
      .post(`http://127.0.0.1:8000/createtweet/`, data, {
          headers: {
              "Content-Type": "multipart/form-data",
              'Authorization':'Bearer '+String(authToken.access)
          },
      }).then((res) => {
        getdata();
        setImage(null);
        setContent("");
          return res;
      }).catch((error) => {
          return error.response;
      });
  }


  return (
    <Container className="App">

  <Row>
    <Col>1 of 3</Col>
    <Col xs={6}>
    <h3 >Home</h3>
    <h5>{user.username}</h5>
    {user&&<Form>
  <Form.Group className="mb-3" controlId="formBasicEmail">
    <Form.Label>Tweet Content</Form.Label>
    <Form.Control as="textarea" rows="3" placeholder="Type here ....." value={content} onChange={(e)=>setContent(e.target.value)}>

    </Form.Control>

  </Form.Group>


  <Form.Group className="mb-3" controlId="formBasicfile">
  <Form.Label>Insert Image</Form.Label>
    <Form.Control type="file"  accept="image/jpeg,image/png,image/gif" onChange={(e)=>handleimg(e)}/>
  </Form.Group>
  <Button variant="primary" type="submit" onClick={(e)=>handleSubmit(e)}>
    Tweet
  </Button>
</Form>}



<div>

   {data&&data.map(({image,content,id})=>(
    <Card key={id} style={{ width: "100%" }}>
    {image&&<Card.Img variant="top" src={`http://127.0.0.1:8000/${image}`}/>}

  <Card.Body>
    {/* <Card.Title>{user}</Card.Title> */}
    {content&&<Card.Text>
      {content}
    </Card.Text>}

    <Button variant="primary">Retweet</Button>
  </Card.Body>
</Card>
   ))}
</div>
    </Col>
    <Col>3 of 3</Col>
  </Row>

</Container>

  );
}

export default Home;
