import React from "react";

import Button from 'react-bootstrap/Button';
import {useState,useEffect} from "react";
import axios from "axios"

function App() {
  const [data,setData]=useState(null)
  const [content,setContent]=useState("")
  const [image,setImage]=useState(null)
  async function  getdata (){
    // let data;
   await axios.get(`http://127.0.0.1:8000/`)
      .then(res => {
        const tweets = res.data;
        // data=res.data;
        setData(tweets)
        // setloading(true);
        // console.log(persons)
        // console.log(data)
      })
      // console.log(data);

  }

  useEffect(()=>{
    getdata()},[]

  );
  if(data){
    console.log(data)
  }
  function handleimg(e){
    // setImgUrl((e.target.files[0]))
    console.log(e.target.files[0])
    setImage(e.target.files[0]);
  }
  function handleSubmit(){
      // let productimg=URL.createObjectURL(imgUrl)
    let data={content,image}
    // console.log(data);
    const myNewModel =  axios
      .post(`http://127.0.0.1:8000/createtweet/`, data, {
          headers: {
              "Content-Type": "multipart/form-data",
          },
      }).then((res) => {
        getdata();
          return res;
      }).catch((error) => {
          return error.response;
      });
  }


  return (
    <div className="App">
      <input type="text"  name="content" value={content} onChange={(e)=>setContent(e.target.value)}/>
      <input type="file" name="image" accept="image/jpeg,image/png,image/gif" onChange={(e)=>handleimg(e)}/>
      <input type="submit" onClick={handleSubmit}/>
    </div>
  );
}

export default App;
