import React from "react";

import Button from 'react-bootstrap/Button';
import {Form} from 'react-bootstrap'
import {useState,useEffect} from "react";
import axios from "axios"

function App() {
  const [data,setData]=useState(null)
  const [content,setContent]=useState("")
  const [image,setImage]=useState(null)
  async function  getdata (){
    // let data;
   await axios.get(`http://127.0.0.1:8000/`)
      .then(res => {
        const tweets = res.data;
        // data=res.data;
        setData(tweets)
        // setloading(true);
        // console.log(persons)
        // console.log(data)
      })
      // console.log(data);

  }

  useEffect(()=>{
    getdata()},[]

  );
  if(data){
    console.log(data)
  }
  function handleimg(e){
    // setImgUrl((e.target.files[0]))
    console.log(e.target.files[0])
    setImage(e.target.files[0]);
  }
  function handleSubmit(e){
      // let productimg=URL.createObjectURL(imgUrl)
      e.preventD
    let data={content,image}
    console.log(data);
    const myNewModel =  axios
      .post(`http://127.0.0.1:8000/createtweet/`, data, {
          headers: {
              "Content-Type": "multipart/form-data",
          },
      }).then((res) => {
        getdata();
          return res;
      }).catch((error) => {
          return error.response;
      });
  }


  return (
    <div className="App">
    <Form>
  <Form.Group className="mb-3" controlId="formBasicEmail">
    <Form.Label>Tweet Content</Form.Label>
    <Form.Control type="text" placeholder="Type here" value={content} onChange={(e)=>setContent(e.target.value)}/>

  </Form.Group>


  <Form.Group className="mb-3" controlId="formBasicfile">
  <Form.Label>Insert Image</Form.Label>
    <Form.Control type="file"  accept="image/jpeg,image/png,image/gif" onChange={(e)=>handleimg(e)}/>
  </Form.Group>
  <Button variant="primary" type="submit" onClick={(e)=>handleSubmit(e)}>
    Submit
  </Button>
</Form>

    </div>
  );
}

export default App;
