import React from "react";
import {useState,useEffect,createContext } from "react";
import axios from "axios"
import {Form,Container,Col,Row,Card,Button} from 'react-bootstrap'
const UserContext = createContext();


function Signin(){

    // console.log(Auth)
    const [username,setUsername]=useState("")
    const [password,setPassword]=useState("")
    const [isAuthenticated,setisAuthenticated]=useState
    function handleSubmit(e){
        // let productimg=URL.createObjectURL(imgUrl)
        e.preventDefault()
      let data={username,password}
      console.log(data);
      const myNewModel =  axios
        .post(`http://127.0.0.1:8000/signin/`, data, {
            headers: {
                "Content-Type": "multipart/form-data",
            },
        }).then((res) => {

            return res;
        }).catch((error) => {
            console.log("errror")
            return error.response;
        });
    }
    return (
        <UserContext.Provider value={{ username,isAuthenticated }}>

        <Form>
  <Form.Group className="mb-3" controlId="formBasicEmail">
    <Form.Label>Username</Form.Label>
    <Form.Control type="text" value={username} onChange={(e)=>setUsername(e.target.value)} placeholder="Enter a Unique Username" />

  </Form.Group>

  <Form.Group className="mb-3" controlId="formBasicPassword">
    <Form.Label>Password</Form.Label>
    <Form.Control type="password" value={password} onChange={(e)=>setPassword(e.target.value)} placeholder="Password" />
  </Form.Group>


  <Button variant="primary" onClick={(e)=>handleSubmit(e)}>
    Submit
  </Button>
</Form>
    </UserContext.Provider>
    );
}

export default Signin
